mapper.select:
select order_num,userid,gCode,gName,gPrice,gSize,gAmount,gImage1,orderName,post1,post2,addr1,addr2,phone,payMethod,to_char(orderday,'YYYY-MM-dd') orderday
from orderinfo
where userid=#{userid}
and to_char(orderday,'yyyy-mm-dd') between #{start_date} and #{end_date}
and gName like '%'||#{searchValue}||'%'

select count(*) from orderinfo
where userid=#{userid}
and to_char(orderday,'yyyy-mm-dd') between #{start_date} and #{end_date}

select count(*) from orderinfo
where userid=#{userid}
and to_char(orderday,'yyyy-mm-dd') between #{start_date} and #{end_date}
and gName like '%'||#{searchValue}||'%'

select order_num,userid,gCode,gName,gPrice,gSize,gAmount,gImage1,orderName,post1,post2,addr1,addr2,phone,payMethod,to_char(orderday,'YYYY-MM-dd') orderday
from orderinfo
where order_num=#{order_num}
